  //                   citiesArray.push(response.capital);
  //                   // setCountry(response.name.common);
  //                   // console.log(countriesArray);
  //                 }
  //                 else {
  //                   console.log('response was undefined')
  //                 }
  //               })
  //             }
  //       )

  // }, []);

  useEffect(() => {
    getAllTheData();
  }, [])

  const getAllTheData = () => {
    fetch(searchForAll, {
      mode: "cors"
    })
    .then(res => res.json())
    .then(
            data => {
            data.map(response => {
              if (response.name.common !== undefined & response.capital !== undefined) {
                // countriesArray.push(response.name.common);
                // citiesArray.push(response.capital);
                dataArray.push(response);
              }
              else {
                console.log('response was undefined')
              }

            })
            // setCountriesList(countriesArray);
            // setCitiesList(citiesArray);
            setDataList(dataArray);
            // console.log(dataArray[0].name.common);
            // console.log(dataArray[0].capital[0]);
          }
    )
  }


  // useEffect(() => {
  //   fetch(`https://restcountries.com/v3.1/name/${country}`, {
