// import CountryRow from './CountryRow';


const searchForAll = 'http://localhost:3000/api';

function App() {
  const [data, setData] = useState([]);
  const [country, setCountry] = useState([]);
  const [city, setCity] = useState('London');

  const countriesArray = [];
  const citiesArray = [];

  useEffect(() => {
        fetch(searchForAll, {
          mode: "cors"
        })
        .then(res => res.json())
        .then(
                data => {
                data.map(response => {
                  if (response.name.common !== undefined & response.capital !== undefined) {
                    // setCountry(response.name.common);
                    // setCity(response.capital);
                    countriesArray.push(response.name.common);
                    citiesArray.push(capital);
                    // setCountry(response.name.common);
